import styled from "styled-components";

export const PostContainer = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 30px;
  background: #fff;
  margin: 30px;

  img {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    border: 1px solid #eee;
  }
  strong {
    align-self: flex-start;
    margin-left: 10px;
  }
  small {
    align-self: flex-start;
    margin-left: 10px;
    align-self: flex-end;
  }
`;
