import React, { useState } from "react";
import Comment from "../Comment";
const [posts, getPosts] = useState([
  {
    posts: [
      {
        id: 1,
        author: {
          name: "Julio Alcantara",
          avatar: "http://url-da-imagem.com/imagem.jpg"
        },
        date: "04 Jun 2019",
        content: "Pessoal, alguém sabe se a Rocketseat está contratando?",
        comments: [
          {
            id: 1,
            author: {
              name: "Diego Fernandes",
              avatar: "http://url-da-imagem.com/imagem.jpg"
            },
            content: "Conteúdo do comentário"
          }
        ]
      },
      {
        id: 2
        // Restante dos dados de um novo post
      }
    ]
  }
]);
import {
  PostContainer,
  PostStructure,
  InfoUser,
  Question,
  Divisor
} from "./styles";

export default function Post() {
  return (
    <PostStructure>
      <PostContainer>
        <img
          src="https://vignette.wikia.nocookie.net/naruto/images/e/e7/Sasuke_epi_319.png/revision/latest?cb=20130629210647&path-prefix=pt-br"
          alt="Sasuke"
        />
        <InfoUser>
          <strong>Julio Alcantara</strong>
          <small>04 de Junho 2019</small>
          <Question>
            Pessoal, alguém sabe se a Rocketseat está contratando?
          </Question>
        </InfoUser>
      </PostContainer>
      <Divisor />
      <Comment></Comment>
    </PostStructure>
  );
}
