import styled from "styled-components";

export const CommentContainer = styled.div`
  display: flex;
  flex-direction: column;
  margin: 10px;

  img {
    height: 30px;
    width: 30px;
    border-radius: 50%;
    border: 1px solid #eee;
  }
  strong {
    font-weight: 600;
    font-size: 16px;
  }
  p {
    font-size: 16px;
  }
`;
