import styled from "styled-components";

export const PostContainer = styled.div`
  display: flex;
  flex-direction: row;
  margin-top: 30px;
  background: #fff;
  margin-left: 30px;
  img {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    border: 1px solid #eee;
  }
`;

export const InfoUser = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 10px;
  align-self: flex-start;
  strong {
    flex: 1;
    font-size: 18px;
    margin-left: 10px;
    font-weight: 600;
  }
  small {
    font-size: 12px;
    color: #333;
  }
`;
